import { CartActionTypes } from "./cart.types";

const INITIAL_STATE = {
  hidden: true
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CartActionTypes.TOGGLE_CART:
      return {
        ...state, //everything else on the state
        hidden: !state.hidden
      };
    default:
      return state;
  }
};

export default cartReducer;
