export const addItemToCart = (items, itemToAdd) => {
  const existingItem = items.find(cartItem => cartItem.id === itemToAdd.id);
  if (existingItem) {
    return items.map(cartItem =>
      cartItem.id === itemToAdd.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    );
  }

  return [...items, { ...itemToAdd, quantity: 1 }];
};

export const removeItemToCart = (items, itemToRemove) => {
  const existingItem = items.find(cartItem => cartItem.id === itemToRemove.id);
  if (existingItem.quanitity === 1) {
    return items.filter(cartItem => cartItem.id !== itemToRemove.id);
  }
  return items.map(cartItem =>
    cartItem.id === itemToRemove.id
      ? { ...cartItem, quantity: cartItem.quantity - 1 }
      : cartItem
  );
};
